<?phpclass CarrierOrder extends ObjectModel{    public $name;    public $description;    public static $definition = array(        'table' => 'carrier_order',        'primary' => 'id_carrier_order',        'multilang' => false,        'fields' => array(            'name' => array('type' => self::TYPE_STRING, 'validate' => 'isString'),            'description' => array('type' => self::TYPE_STRING, 'validate' => 'isString'),        ),    );    public static function getCarriersOrder()    {        $sql = new DbQuery();        $sql->select('*');        $sql->from('carrier_order');        return Db::getInstance()->executeS($sql);    }    public static function getCarrierOrderByOrder($id_order)    {        $sql = new DbQuery();        $sql->select('*');        $sql->from('carrier_order_combination');        $sql->where('id_order = ' . $id_order);        $results = Db::getInstance()->executeS($sql);        $carrier_order_combination = [];        foreach ($results as $result) {            $carrier_order_combination[$result['id_carrier_order']] = 1;        }        return $carrier_order_combination;    }}