<?phpclass PackagingOrder extends ObjectModel{    public $name;    public $description;    public static $definition = array(        'table' => 'packaging_order',        'primary' => 'id_packaging_order',        'multilang' => false,        'fields' => array(            'name' => array('type' => self::TYPE_STRING, 'validate' => 'isString'),            'description' => array('type' => self::TYPE_STRING, 'validate' => 'isString'),        ),    );    public static function getPackagingsOrder()    {        $sql = new DbQuery();        $sql->select('*');        $sql->from('packaging_order');        return Db::getInstance()->executeS($sql);    }    public static function getPackagingOrderByOrder($id_order)    {        $sql = new DbQuery();        $sql->select('*');        $sql->from('packaging_order_combination');        $sql->where('id_order = ' . $id_order);        $results = Db::getInstance()->executeS($sql);        $packagings_order_combination = [];        foreach ($results as $result) {            $packagings_order_combination[$result['id_packaging_order']] = 1;        }        return $packagings_order_combination;    }}