<?phprequire_once _PS_MODULE_DIR_ . 'rdorderfields/classes/CarrierOrder.php';class AdminCarrierOrderController extends ModuleAdminController{    public function __construct()    {        $this->table = 'carrier_order';        $this->className = 'CarrierOrder';        $this->fields_list = array(            'id_carrier_order' => array(                'title' => 'ID',                'width' => 25,            ),            'name' => array(                'title' => 'Nom',                'align' => 'center',                'filter_key' => 'a!name',            ),            'description' => array(                'title' => 'Description',                'filter_key' => 'a!description',                'align' => 'center',            ),        );        $this->bootstrap = true;        $this->addRowAction('edit');        $this->addRowAction('delete');        $this->show_page_header_toolbar = false;        $this->show_toolbar = false;        $this->show_toolbar_options = false;        parent::__construct();    }    public function renderForm()    {        $this->fields_form = array(            'input' => array(                array(                    'type' => 'text',                    'label' => 'Nom',                    'name' => 'name',                    'required' => true                ),                array(                    'type' => 'textarea',                    'label' => 'Description',                    'name' => 'description',                ),            ),            'submit' => array('title' => 'Enregistrer')        );        $parent = AdminController::renderForm();        return $parent;    }}